#include "main.hpp"

Main::Main(char *inputCSV1, char *inputCSV2, char *inputCSV3, char *inputCSV4)
{
    majorCSVFile = inputCSV1;
    studentCSVFile = inputCSV2;
    courseCSVFile = inputCSV3;
    professorCSVFile = inputCSV4;
}

void Main::Run()
{
    SuperCommand program(majorCSV);
    while (true)
    {
        try
        {
            program.GetInput();
            SelectSubCommand(argv[1], argv[2], argv[3], argv[4], program.GetSuperCommand(), program.GetSubCommand(), program.GetArguments());
        }
        catch (ErrorHandler &error)
        {
            error.GetErrorMassage();
        }
    }
}
